import React, { Component } from 'react'
import { CardBody, Col, Row, Spinner, Button } from 'reactstrap'
import Audio1Slide5 from '../../assets/audio/Audio1Slide5.mp4'
import gif from '../../assets/img/s/1.mp4'
import ReactPlayer from 'react-player'


class TA1View extends Component {
  loading = () => (
    <div className="animated fadeIn pt-3 text-center "><Spinner color="danger" /></div>
  )
  playAudio() {
    const audioEl = document.getElementsByClassName("audio-element")[0]
    audioEl.play()
  }

  constructor(props) {
    super(props);
    this.state = {
      showDiv: false
    };
    this.open = this.open.bind(this);
  }
  open() {
    const { showDiv } = this.state;
    this.setState({
      showDiv: !showDiv,
    });
  }


  render() {
    return (
      <div className="animated fadeIn">
        {/* <CardHeader className="text-left " style={{paddingBottom:"0px"}}>
          <h5> GESTION DE VANTAS, VENCIMIENTO DE OBJECIONES.</h5>
          <h5> &nbsp;&nbsp;-1.1 Que es el vencimiento o  manejo de objeciones.</h5>
        </CardHeader> */}
        <CardBody >
          <Row className="centrado-fila">
            <Col xs="3">

              <audio className="audio-element">
                <source src={Audio1Slide5}></source>
              </audio>
              <ReactPlayer width={400} playing={true} url={gif}
                style={{ cursor: "pointer" }}
                onClick={this.playAudio}
              />
 <Button onClick={this.open}>Open Modal</Button>
              {this.state.showDiv &&
                <div className="alert alert-success alert-dismissible notification">
                  <button type="button" className="close" data-dismiss="alert" onClick={this.open}>&times;</button>
                  <strong>Success!</strong> Indicates a successful or positive action.
                </div>}
            </Col>
            <Col xs="4">
              <div className="slideInUp">
                <div className="globo ">
                  <p>
                    Hola soy Luz y te guiare en el curso “<b>AUTENTICACIÓN DE CASOS ATÍPICOS</b>”<br />
                    ¡Comencemos!
                  </p>
                </div>
              </div><br /><br /><br /><br /><br /><br /><br /><br /><br /><br /><br /><br /><br /><br />


            </Col>
          </Row>
        </CardBody>
      </div>
    )
  }
}
export default TA1View
