import React, { Component } from 'react'
import { CardBody, Col, Row, Spinner } from 'reactstrap'
import gif from '../../assets/img/s/1.mp4'
import ReactPlayer from 'react-player'

class InstruccionView extends Component {
  loading = () => (
    <div className="animated fadeIn pt-3 text-center "><Spinner color="danger" /></div>
  )

  constructor(props) {
    super(props);
    this.state = {
      showDiv: false
    };
    this.open = this.open.bind(this);
  }
  open() {
    const { showDiv } = this.state;
    this.setState({
      showDiv: !showDiv,
    });
  }

  playAudio() {
    const audio = document.getElementsByClassName("audio-element")
    audio.play()
  }

  render() {
    return (
      <div className="animated fadeIn">
        {/* <CardHeader className="text-left">
          <h3>Instrucciones</h3>
        </CardHeader> */}
        <CardBody className="">
          <Row className="centrado-fila">
            <Col xs="3">
              <div onClick={() => {
   this.playAudio();
   this.open();
}}>
                <ReactPlayer
                  width={400}
                  url={gif}
                  style={{ cursor: "pointer" }}
                  className="audio-element"
                />
              </div>
            </Col>
            <Col xs="4">
              {this.state.showDiv &&
                <div className="slideInUp">
                  <div className="globo ">
                    <p>
                      Hola soy Luz y te guiare en el curso “<b>AUTENTICACIÓN DE CASOS ATÍPICOS</b>”<br />
                      ¡Comencemos!
                    </p>
                  </div>
                </div>}
              <br /><br /><br /><br /><br /><br /><br /><br /><br /><br /><br /><br /><br /><br />
            </Col>
          </Row>
        </CardBody>
      </div>
    )
  }
}
export default InstruccionView
