import React, { Component } from 'react'
import { CardBody, Col, Row, Spinner } from 'reactstrap'
import Audio1Slide5 from '../../assets/audio/Audio1Slide5.mp4'
import gif from '../../assets/img/s/1.mp4'
import ReactPlayer from 'react-player'


class TA1View extends Component {
  loading = () => (
    <div className="animated fadeIn pt-3 text-center "><Spinner color="danger" /></div>
  )
  playAudio() {
    const audioEl = document.getElementsByClassName("audio-element")[0]
    audioEl.play()
  }

  constructor() {
    super();
    this.state = { checked: false };
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(checked) {
    this.setState({ checked });
  }
  render() {
    return (
      <div className="animated fadeIn">
        {/* <CardHeader className="text-left " style={{paddingBottom:"0px"}}>
          <h5> GESTION DE VANTAS, VENCIMIENTO DE OBJECIONES.</h5>
          <h5> &nbsp;&nbsp;-1.1 Que es el vencimiento o  manejo de objeciones.</h5>
        </CardHeader> */}
        <CardBody >
          <Row className="centrado-fila">
            <Col xs="3">

              <audio className="audio-element">
                <source src={Audio1Slide5}></source>
              </audio>
              <ReactPlayer width={300} playing={true} url={gif} 
               style={{ cursor: "pointer" }}
               onClick={this.playAudio}
              />

            </Col>
            <Col xs="5">
              <div className="slideInUp">
                <div className="globo ">
                  <p>
                    Hola soy Luz y te guiare en el curso “<b>AUTENTICACIÓN DE CASOS ATÍPICOS</b>”<br />
                    ¡Comencemos!
                  </p>
                </div>
              </div><br />


            </Col>
          </Row>
        </CardBody>
        <div>
        <div className="col-md-6  mb-2">
          <div
            className="btn-group btn-group-sm"
            role="group"
            aria-label="Basic example"
          >
            {/* Este es el boton 1 */}
            <button
              type="button"
              id="btn-nquote"
              className="btn btn-success btn-sm"
              onClick={() => this.handleChange(false)}
            >
              Normal Quote
            </button>

            {/* Este es el boton 2 */}
            <button
              type="button"
              id="btn-flex"
              className="btn btn-unique btn-sm"
              onClick={() => this.handleChange(true)}
            >
              Flex Quote
            </button>
          </div>
        </div>
        <span>
          {this.state.checked ? (
            <div /* Este es el div 1 */ className="redd" />
          ) : (
            <div /* Este es el div 2 */ className="red2" />
          )}
        </span>


      </div>
      </div>
    )
  }
}
export default TA1View
