import React, { Component } from 'react'
import { CardBody, Col, Row, Spinner, CardHeader, Button, Form } from 'reactstrap'
import Modal1 from './components/modales'
import excelente from '../../assets/img/s/excelente.png'
import intentalodenuevo from '../../assets/img/s/intentalodenuevo.png'
import API_CCS from '../../services/API_CCS'
const API = new API_CCS()

class TA12View extends Component {
  loading = () => (
    <div className="animated fadeIn pt-3 text-center "><Spinner color="danger" /></div>
  )


  constructor(props) {
    super(props)
    this.Auth = new AuthService()
    this.state = {
      modal: false,
        acierto1: "",
        acierto2: "",
        acierto3: "",
        id_ccs: this.Auth.getProfile().id_ccs,
        form: 'casosAtipicos',
    }

    this.toggleM = this.toggleM.bind(this)
  }
  toggleM(modIndex) {
    this.setState(prevState => ({
      modal: !prevState.modal,
    }))

    this.setState({ modalIndex: modIndex })
  }

  onChange(e) {
    this.setState({
      [e.target.name]: e.target.checked,
    })
  }

  async onSave(e) {
    try {
      var respuesta = await API.guardaActividad(this.state)
      alert('Se guardo la encuesta número ' + respuesta[0].id)
    } catch (err) {
      console.log('loggea si hay un error')
    }
  }

  render() {
    return (
      <div className="animated fadeIn">
        <CardHeader className="text-left" style={{ paddingBottom: "0px" }}>
          <h5> AUTENTICACIÓN CASOS ATÍPICOS</h5>
        </CardHeader>
        <CardBody >
          <Row className="centrado-fila">
            <Col xs="12">
              <h1>1.  ¿Qué es la autenticación?</h1>
            </Col>

            <Col xs="5">
              <div>
              <Form onSubmit={this.handleSubmit}>
                <Button className="btn-primary" onClick={() => this.toggleM(1)}>
                  A) Es el proceso mediante el cual identificamos de manera exclusiva a un beneficiario.
                </Button><br /><br />
                <Button className="btn-primary" onClick={() => this.toggleM(2)}>
                  B) Es el proceso mediante el cual se activa la tarjeta de un beneficiario.
                </Button><br /><br />
                <Button className="btn-primary" onClick={() => this.toggleM(3)}>
                  C) Es el proceso mediante el cual se desbloquea la tarjeta de un beneficiario.
                </Button>
              </Form>
              </div>
              <div>
                {this.state.modal ? (
                  <Modal1
                    cerrar={() => this.toggleM().onSave() }
                    // title={
                    //   this.state.modalIndex === 1
                    //     ? '¡EXCELENTE!'
                    //     : this.state.modalIndex === 2
                    //       ? '¡INTENTALO DE NUEVO!'
                    //       : this.state.modalIndex === 3
                    //         ? '¡INTENTALO DE NUEVO!'
                    //         : null
                    // }

                    body={
                      this.state.modalIndex === 1 ? (
                        <div className="centrado-fila">
                          <div className="centrado-fila">
                            <h1 className="text-success">¡EXCELENTE!</h1>
                          </div>
                          <div className="centrado-fila">
                            <img
                              src={excelente}
                              style={{ width: 300 }}
                              alt="excelente"
                              className="img-fluid  animated  bounceInRight delay-2s"
                            />
                          </div>
                        </div>

                      ) : this.state.modalIndex === 2 ? (
                        <div className="centrado-fila">
                          <div className="centrado-fila">
                            <h1 className="text-dange">¡INTENTALO DE NUEVO!</h1>
                          </div>
                          <div className="centrado-fila">
                            <img
                              src={intentalodenuevo}
                              style={{ width: 300 }}
                              alt="intentalodenuevo"
                              className="img-fluid  animated  bounceInRight delay-2s"
                            />
                          </div>
                        </div>
                      ) : this.state.modalIndex === 3 ? (
                        <div className="centrado-fila">
                          <div className="centrado-fila">
                            <h1 className="text-dange">¡INTENTALO DE NUEVO!</h1>
                          </div>
                          <div className="centrado-fila">
                            <img
                              src={intentalodenuevo}
                              style={{ width: 300 }}
                              alt="intentalodenuevo"
                              className="img-fluid  animated  bounceInRight delay-2s"
                            />
                          </div>
                        </div>
                      ) : null
                    }
                  />
                ) : null}
              </div>
            </Col>
          </Row>
        </CardBody>
      </div>
    )
  }
}
export default TA12View
